import React from "react";

export const Answer_section = (props: any) => {
    return (<div className="answer-section">
    {props.questions.answerOptions.map((item) => (<button
      onClick={() => props.handleAnswerOptionClick(item.isCorrect)}
    >{item.answerText}</button>
    ))}
  </div>);
};
