import React, {useState} from "react";
import './App.css';
import Data from './components/Data.json';
import {Result} from './components/Result';
import {Statusbar} from './components/Statusbar';
import { refresh__btn } from "./components/refresh__btn";



export const App = () => {
  const questions = Data;

  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [score, setScore] = useState(0);
  const [showScore, setShowScore] = useState(false);

  const handleAnswerOptionClick = (isCorrect: boolean) => {

    if (isCorrect) {
      setScore(score => score + 1);
    }


    const nextQuestion = currentQuestion + 1;

    if (nextQuestion < questions.length) {

      setCurrentQuestion(nextQuestion);

    } else {

      setShowScore(true);

    }

  };

  const handleRefresh = () => {

    setCurrentQuestion(0);
    setScore(0);
    setShowScore(false);

  };


  let status = (score < questions.length) ? 'Не оч((' : 'Красава';

  return (
    <div className="app">
      {showScore
        ?
        <div className="section__score">
          <Result score={score} questions={questions} />
          <Statusbar score={score} questions={questions} status={status} />
          <Refresh__btn handleRefresh={handleRefresh}/>
        </div>
        : <div className='quizz'>
          <div className='question-section'>
            <div className="question-count">
              <span> Вопрос {currentQuestion + 1} </span> /{questions.length}
            </div>
            <div className="question-text">{questions[currentQuestion].questionText}
            </div>
            <div className="answer-section">
              {questions[currentQuestion].answerOptions.map(item => (<button



                onClick={() => handleAnswerOptionClick(item.isCorrect)}


              >{item.answerText}</button>
              )
              )}
            </div>
          </div>

        </div>}</div>


  );
}
