import React, {useState} from "react";
import './App.css';
import Data from './components/Data.json';
import {Result} from './components/Result';
import {Statusbar} from './components/Statusbar';
import {Refresh_btn} from './components/Refresh_btn';
import {Question_count} from './components/Question_count';
import {Question_text} from "./components/Question_text";
import {Answer_section} from "./components/Answer_section";
import { Question_section } from "./components/Questuion_section";



export const App = () => {
  const questions = Data;

  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [score, setScore] = useState(0);
  const [showScore, setShowScore] = useState(false);

  const handleAnswerOptionClick = (isCorrect: boolean) => {

    if (isCorrect) {
      setScore(score => score + 1);
    }


    const nextQuestion = currentQuestion + 1;

    if (nextQuestion < questions.length) {

      setCurrentQuestion(nextQuestion);

    } else {

      setShowScore(true);

    }

  };

  const handleRefresh = () => {

    setCurrentQuestion(0);
    setScore(0);
    setShowScore(false);

  };


  let status = (score < questions.length) ? 'Не оч((' : 'Красава';

  return (
    <div className="app">
      {showScore
        ?
        <div className="section__score">
          <Result score={score} questions={questions} />
          <Statusbar score={score} questions={questions} status={status} />
          <Refresh_btn handleRefresh={handleRefresh}/>
        </div>
        : <div className='quizz'>
          <Question_section currentQuestion={currentQuestion} questions={questions} currQuestions={questions[currentQuestion]} handleAnswerOptionClick={handleAnswerOptionClick}/>
          <div className='question-section'>
            <Question_count currentQuestion={currentQuestion} questions={questions}/>
            <Question_text currQuestions={questions[currentQuestion]}/>
            <Answer_section currQuestions={questions[currentQuestion]} handleAnswerOptionClick={handleAnswerOptionClick}/>
          </div>

        </div>
        }
        </div>


  );
}
