export const Question_text = (props) => {
    return (<div className="answer-section">
    {questions.answerOptions.map(item => (<button
      onClick={() => handleAnswerOptionClick(item.isCorrect)}
    >{item.answerText}</button>
    ))}
  </div>);
};
